import { useState } from "react"
import { Form, FormFeedback, FormGroup, Input, InputGroup, Label } from "reactstrap"
import validator from 'validator';

const Login = () => {

    const [emailError, setEmailError] = useState(false);
    const [email, setEmail] = useState("");

    const handleEmail = (event) => {
        setEmail(event.target.value)
        if (validator.isEmail(email)){
            setEmailError(false)
        } else {
            setEmailError(true)
        }
    }

    return(
        <>
            <Form>
                <FormGroup>
                    <Label for="email">Email</Label>
                    <Input 
                        type="text"
                        placeholder="Enter Email"
                        value={email}
                        onChange={handleEmail}
                        invalid={email !=="" && emailError}
                        valid={email !=="" && !emailError}
                    />
                    <FormFeedback invalid>Email is InValid</FormFeedback>
                    <FormFeedback valid>Email is Valid</FormFeedback>
                </FormGroup>
                <FormGroup>
                    <InputGroup>
                        <Label for="password">Password</Label>
                        <Input 
                            type="password"
                            placeholder="Email "
                        />
                    </InputGroup>
                </FormGroup>
            </Form>
        </>
    )
}
export default Login
